  $('#pause_button').off('click').on('click', function() {

      if (isPlaying) {
          // Pause
          isPlaying = false;
          // $(this).text('Play');
          $(this).html('<i class="fas fa-play"></i>');

          // Envoie la commande de pause via MQTT
          var data = {
              playpause: '0',
              song: '',
              oled: ''
          };

          data.name = '';
          data.random = '';
          data.redo = '';

          var message = {
              mac_address: selectedMac,
              data: data
          };
          var jsonMessage = JSON.stringify(message);
          publishMessage('ESP32', jsonMessage);
      } else {
          // Reprise
          isPlaying = true;
          // $(this).text('Pause');
          $(this).html('<i class="fas fa-pause"></i>');
          // Effectuer les actions de reprise nécessaires

          // Envoie la commande de reprise via MQTT
          var data = {
              playpause: '1',
              song: '',
              oled: ''
          };
          data.name = '';
          data.random = '';
          data.redo = '';
          var message = {
              mac_address: selectedMac,
              data: data
          };
          var jsonMessage = JSON.stringify(message);
          publishMessage('ESP32', jsonMessage);
      }
  });

