  $(document).ready(function() {
    var isRedoClicked = false;
    var isRandomClicked = false;
    var data = {};

    function redo() {
      if (!isRedoClicked) {
        // Ajoutez ici le code pour effectuer l'action "Refaire"
        console.log("Action Aléatoire activé");
        isRedoClicked = true;
        data = {
          name:'',
          playpause: '',
          redo: '1',
          random:'',
          song: '',
          oled: ''
        };
        $('#redoButton').addClass('active');
      } else {
        // Ajoutez ici le code pour annuler l'action "Refaire"
        console.log("Action Refaire annulée");
        isRedoClicked = false;
        data = {
          name:'',
          playpause: '',
          redo: '0',
          random:'',
          song: '',
          oled: ''
        };
        $('#redoButton').removeClass('active');
      }
    var message = {
        mac_address: selectedMac,
        data: data
    };
    var jsonMessage = JSON.stringify(message);
    publishMessage('ESP32', jsonMessage);
    }
  
    function random() {
      if (!isRandomClicked) {
        // Ajoutez ici le code pour effectuer l'action "Aléatoire"
        console.log("Action Aléatoire activé");
        isRandomClicked = true;
        $('#randomButton').addClass('active');
      } else {
        // Ajoutez ici le code pour annuler l'action "Aléatoire"
        console.log("Action Aléatoire annulée");
        isRandomClicked = false;
        $('#randomButton').removeClass('active');
      }

    }


    // Attacher les événements "click" initiaux
    $('#redoButton').on('click', redo);
    $('#randomButton').on('click', random);
  });

  

